import React from 'react'
import { Card, CardContent, List, ListItem, ListItemText, Typography } from '@mui/material'

import card_data from '../../data/card-data'

const questionOptions_styles = {
    display: 'grid',
    gridTemplateColumns: 'repeat(6, 175px)',
    gridGap: '10px',
    padding: '10px',
    textAlign: 'center',
}
const categories = card_data.categories

const QuestionOptions = () => {
    return (
        <div style={questionOptions_styles}>
            { categories.map((category) => {
               return (
                    <>
                        { category.answers.map((questions) => {
                            return (
                                <Card key={questions.cardId} style={{gridColumn: questions.categoryId, gridRow: questions.cardId}}>
                                    <CardContent>
                                        <Typography variant='body1' sx={{fontFamily: 'Arial'}}>
                                            category: {questions.categoryId}
                                        </Typography>
                                        <List>
                                            <ListItem>
                                                <ListItemText primary={questions.answer.a} />
                                            </ListItem>
                                            <ListItem>
                                                <ListItemText primary={questions.answer.b} />
                                            </ListItem>
                                            <ListItem>
                                                <ListItemText primary={questions.answer.c} />
                                            </ListItem>
                                            <ListItem>
                                                <ListItemText primary={questions.answer.d} />
                                            </ListItem>
                                        </List>
                                    </CardContent>
                                </Card>
                            )
                        } )}
                    </>
                )
            })}
        </div>     
    )
}

export default QuestionOptions;
