import React from 'react'
import { Card, CardContent, Typography } from '@mui/material'

import card_data from '../../data/card-data'

const pointCard_styles = {
    display: 'grid',
    gridTemplateColumns: 'repeat(7, 175px)',
    gridGap: '10px'
}

const PointCard = () => {
    const categories = card_data.categories
    return (
        <div style={pointCard_styles}>
            { categories.map((category) => {
               return (
                    <>
                        { category.answers.map((points) => {
                            console.log(points)
                            return (
                                <Card key={points.cardId} style={{gridColumn: points.categoryId}}>
                                    <CardContent>
                                        <Typography variant='h4'>
                                            {points.pointValue}
                                        </Typography>
                                    </CardContent>
                                </Card>
                            )
                        } )}
                    </>
                )
            })}
        </div>     
    )

}
export default PointCard;
